"use strict";var db=require("../data/dbConfig.js");function get(){return db("posts")}function getById(e){return db("posts").where({id:e}).first()}function insert(e){return db("posts").insert(e).then(function(e){return getById(e[0])})}function update(e,t){return db("posts").where({id:e}).update(t)}function remove(e){return db("posts").where("id",e).del()}module.exports={get:get,getById:getById,insert:insert,update:update,remove:remove};